import { useState } from "react";
import EventCard from "../../components/EventCard";
import Select from "../../components/Select";
import { useData } from "../../contexts/DataContext";
import Modal from "../Modal";
import ModalEvent from "../ModalEvent";

import "./style.css";

const PER_PAGE = 9;

const EventList = () => {
  const { data, error } = useData();
  const [type, setType] = useState(); // Modification : Initialiser type à null
  const [currentPage, setCurrentPage] = useState(1);
  const filteredEvents = data?.events.filter((event, index) => {
    const startIndex = (currentPage - 1) * PER_PAGE;
    const endIndex = PER_PAGE * currentPage;
    return index >= startIndex && index < endIndex;
  });
  const changeType = (evtType) => {
    setCurrentPage(1);
    setType(evtType);
  };
  const pageNumber = Math.ceil((filteredEvents?.length || 0) / PER_PAGE); // Modification : Utiliser Math.ceil pour arrondir vers le haut
  const typeList = Array.from(new Set(data?.events.map((event) => event.type))); // Modification : Convertir Set en Array

  return (
    <>
      {error && <div>An error occurred</div>} {/* Modification : "An error occured" -> "An error occurred" */}
      {data === null ? (
        "loading"
      ) : (
        <>
          <h3 className="SelectTitle">Catégories</h3>
          <Select
            selection={typeList}
            onChange={(value) => changeType(value)}
 />
          <div id="events" className="ListContainer">
            {filteredEvents.map((event) => (
              <Modal key={event.id} Content={<ModalEvent event={event} />}>
                {({ setIsOpened }) => (
                  <EventCard
                    onClick={() => setIsOpened(true)}
                    imageSrc={event.cover}
                    title={event.title}
                    date={new Date(event.date)}
                    label={event.type}
                  />
                )}
              </Modal>
            ))}
          </div>
          <div className="Pagination">
            {[...Array(pageNumber)].map((_, n) => (
              // eslint-disable-next-line react/no-array-index-key
              <a key={n} href="#events" onClick={() => setCurrentPage(n + 1)}>
                {n + 1}
              </a>
            ))}
          </div>
        </>
      )}
    </>
  );
};

export default EventList;
